INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (17628, '1 Dollar (Bicentennial of the Congress)', '1989 - 1989', 'Silver (.900)', 'The 1989 Congress Silver Dollar was offered in proof version struck at the San Francisco Mint and in uncirculated version struck at the Denver Mint. The maximum authorized mintage across both options was 3 million coins. The silver dollars were sold individually or incorporated into different two and three coin sets and one six coin sets. A pre-issued discount price was available before regular pricing went into effect. 1989-D Uncirculated Congress Silver Dollar Pre-Issue Price: $23.00 Regular Price: $26.00 Final Mintage: 135,203 1989-S Proof Congress Silver Dollar Pre-Issue Price: $25.00 Regular Price: $29.00 Final Mintage: 762,198');
