INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (6846, '½ Dime "Seated Liberty Half Dime" (arrows at date)', '1853 - 1855', 'Silver (.900) (.100 copper)', 'The arrows on both sides of the date mean that the coin weight was reduced (to 19¹∕₅ grains) from the previous issue (20⁵∕₈ grains). "Half dime" is the name for the silver 5 cents coins, later base-metal issues are known as "Nickels." These coins saw frequent circulation in Nicaragua.');
