INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (1346, '1 Dollar "Eisenhower Dollar"', '1971 - 1978', 'Copper-nickel clad copper', 'The standard weight of these coins was 350 grains. All coins of Denver and Philadelphia were stuck for regular circulation. All San Fransisco coins were struck in proof condition for collectors. The 1972 coin occurs in three types: Type 1: Has three islands to the right of Florida: Type 2: The islands are not shown: Type 3: Has three islands beginning to the left of Florida: Mintage resumed in 1977 as KM#A203.');
