INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (268117, '1 Dollar (Christa McAuliffe)', '2021 - 2021', 'Silver (.999)', 'Christa McAuliffe, a high school social studies teacher, was the first participant in the National Aeronautics and Space Administration’s Teacher in Space program. She and six astronauts tragically lost their lives in the Space Shuttle Challenger flight on January 28, 1986.');
