INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (317075, '1 Dime "Seated Liberty Dime" (w/o stars, increased weight)', '1875 - 1891', 'Silver (.900) (.100 copper)', 'The Coinage Act of 12 February, 1873, increased the standard weight of dimes from 38¹∕₅ grains (2.49 grams, KM# 92) to a metric standard of 2½ grams (38.58 grains). These coins saw frequent circulation in Nicaragua. 1875 1876 1890 1891 O 1891 S ');
