INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (155678, '¼ Dollar ''Washington Quarter'' (War in the Pacific National Historical Park, Guam)', '2019 - 2019', 'Silver (.999)', 'The westernmost park of all the National Park Service sites, Guam’s War in the Pacific National Historical Park honors the bravery, courage, and sacrifice of those participating in the campaigns of the Pacific Theater during World War II. The park also conserves and interprets a variety of amazing resources found on the Island of Guam. War in the Pacific National Historical Park has the highest biological diversity of any national park, with both underwater and land areas.');
