INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (189640, '¼ Dollar ''Washington Quarter'' (Tallgrass Prairie National Preserve, Kansas)', '2020 - 2020', 'Silver (.999)', 'The Tallgrass Prairie National Preserve in Kansas encompasses nearly 11,000 acres and is located in the heart of the Flint Hills–the largest expanse of tallgrass prairie left in North America. It is the only unit of the National Park Service dedicated to the rich, natural history of the tallgrass prairie. Authorized in 1996, the enabling legislation for the preserve also directed the National Park Service to interpret the region’s ranching legacy; a portion of the preserve is still grazed by cattle.');
