INSERT INTO us_coins (numista_id, name, years, composition, description) VALUES (55101, '50 Cents "Capped Bust Half Dollar" (Reeded edge)', '1836 - 1837', 'Silver (.8924) (.1076 copper)', 'The Coinage Act of 18 January 1837 decreased the standard weight of half dollars from 208 grains (13.48 grams, KM# 58) to 206¼ grains (13.36 grams, KM# 58a). The fineness was increased from .8924 silver (KM# 58) to .900 silver (KM# 58a). The silver content was unchanged. These were the last coins struck to the old standard.');
